template.code:
var L :TListBoxItem;

|List|.Items.Clear;
|DataSet|.First;
while not |DataSet|.Eof do begin
   L := TListBoxItem.Create(|List|);
   L.Text := |DataSet|.FieldByName('|Text|').AsString;
   L.ItemData.Detail := |DataSet|.FieldByName('|Detail|').AsString;
   L.Height := 40;
   L.StyleLookup := 'listboxitembottomdetail';
   |List|.AddObject(L);
   |DataSet|.Next;
end;
